package bitMap;public class MyBitMap {    private int[] a;    private int maxValue;    public MyBitMap(int maxValue) {        a = new int[1+maxValue/32];        this.maxValue = maxValue;    }    void addValue(int value) throws Exception {        if (value < 0 || value > maxValue) {            throw new Exception("超过bitMap最大值");        }        /*        (value>>5) equals (value/32)        (value%32) equals (value&31) equals(value&0x1f)        */        a[value>>5] |= 1<<(value&31);    }    public boolean exist(int value) throws Exception {        if (value<0||value>maxValue){            throw new Exception("超过最大值");        }        return (a[value>>5]&(1<<(value&0x1f)))!=0;    }}